CREATE TABLE Nhacungcap
(
  MaNCC NVARCHAR(100) NOT NULL,
  TenNCC NVARCHAR(100) NOT NULL,
  SDTNCC NVARCHAR(20) NOT NULL,
  EmailNCC NVARCHAR(MAX) NOT NULL,
  DiachiNCC NVARCHAR(MAX) NOT NULL,
  Masothue NVARCHAR(20) NOT NULL,
  PRIMARY KEY (MaNCC),
  UNIQUE (Masothue)
);

CREATE TABLE Nhanvien
(
  MaNV INT NOT NULL,
  TenNV NVARCHAR(50) NOT NULL,
  NgaySinhNV DATE NOT NULL,
  SoCCCDNV NVARCHAR(20) NOT NULL,
  EmailNV NVARCHAR(MAX) NOT NULL,
  SDTNV NVARCHAR(20) NOT NULL,
  DiachiNV NVARCHAR(MAX) NOT NULL,
  GioiTinh NVARCHAR(12) NOT NULL,
  Chucvu NVARCHAR(20) NOT NULL,
  CONGVIEC NVARCHAR(50) NOT NULL,
  PRIMARY KEY (MaNV),
  UNIQUE (SoCCCDNV)
);

CREATE TABLE Hoadon
(
  MaHD NVARCHAR(20) NOT NULL,
  NgaylapHD DATE NOT NULL,
  SLCBPdon INT NOT NULL,
  GHICHU NVARCHAR(MAX) NULL,
  CPVC MONEY NOT NULL,
  THUE FLOAT NOT NULL,
  MaNCC NVARCHAR(100) NOT NULL,
  MaNV INT NOT NULL,
  PRIMARY KEY (MaHD),
  FOREIGN KEY (MaNCC) REFERENCES Nhacungcap(MaNCC),
  FOREIGN KEY (MaNV) REFERENCES Nhanvien(MaNV)
);

CREATE TABLE VTKHO
(
  MaViTriCB NVARCHAR(10) NOT NULL,
  MaHD NVARCHAR(20) NULL,
  MaNV INT NULL,
  PRIMARY KEY (MaViTriCB),
  FOREIGN KEY (MaHD) REFERENCES Hoadon(MaHD),
  FOREIGN KEY (MaNV) REFERENCES Nhanvien(MaNV)
);

CREATE TABLE CB
(
  MaCB NVARCHAR(100) NOT NULL,
  TenCB NVARCHAR(100) NOT NULL,
  XuatxuCB NVARCHAR(50) NOT NULL,
  DongiaCB MONEY NOT NULL,
  LoaiCB NVARCHAR(MAX) NOT NULL,
  HSD DATE NOT NULL,
  MaHD NVARCHAR(20) NOT NULL,
  PRIMARY KEY (MaCB),
  FOREIGN KEY (MaHD) REFERENCES Hoadon(MaHD)
);

CREATE TABLE CTNK
(
  NgaynhapK DATE NOT NULL,
  MaNK NVARCHAR(20) NOT NULL,
  TINHTRANGCB NVARCHAR(20) NOT NULL,
  MaCB NVARCHAR(100) NOT NULL,
  MaViTriCB NVARCHAR(10) NOT NULL,
  PRIMARY KEY (MaNK),
  FOREIGN KEY (MaCB) REFERENCES CB(MaCB),
  FOREIGN KEY (MaViTriCB) REFERENCES VTKHO(MaViTriCB),
  UNIQUE (MaCB, MaViTriCB)
);
